package org.mark.rmi;import java.rmi.Naming;import java.rmi.RMISecurityManager;public class DisplayPerfectTime {	public static void main(String[] args) throws Exception {		System.setSecurityManager(new RMISecurityManager());		PerfectTimeI t = (PerfectTimeI) Naming				.lookup("//127.0.0.1/PerfectTime");		for (int i = 0; i < 10; i++)			System.out.println("Perfect time = " + t.getPerfectTime());	}}